import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import SosModal from "./SosModal"; // New file

export default function UserDashboard() {
  const navigate = useNavigate();
  const currentUser = JSON.parse(localStorage.getItem("currentUser") || "{}");
  const [showSos, setShowSos] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("currentUser");
    navigate("/");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-blue-100">
      <nav className="bg-white shadow-md px-8 py-4 flex justify-between items-center">
        <h2 className="text-2xl font-black text-gray-900">Community Safety Hub</h2>
        <div className="flex gap-4 items-center">
          <span className="text-gray-700 font-semibold">Hi, {currentUser.name || "User"}!</span>
          <button
            onClick={() => navigate("/")}
            className="text-gray-700 font-semibold hover:text-blue-600 transition"
          >
            Home
          </button>
          <button
            onClick={handleLogout}
            className="px-6 py-2 bg-red-500 text-white font-bold rounded-lg hover:bg-red-600 transition"
          >
            Logout
          </button>
        </div>
      </nav>

      <div className="px-8 py-12">
        <h1 className="text-4xl font-black text-gray-900 mb-8">User Dashboard</h1>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-12">
          {/* ...stats cards as before... */}
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <button
            onClick={() => setShowSos(true)}
            className="bg-gradient-to-r from-red-500 to-red-700 text-white font-bold py-6 rounded-2xl shadow-lg hover:scale-105 transition text-xl"
          >
            ðŸš¨ Report Emergency
          </button>
        </div>

        {/* ...activity feed, etc... */}

        {showSos && (
          <SosModal
            onClose={() => setShowSos(false)}
            onReported={(incident) => {
              // Save incidents to localStorage for demo (append)
              const past = JSON.parse(localStorage.getItem("incidents") || "[]");
              localStorage.setItem("incidents", JSON.stringify([incident, ...past]));
              setShowSos(false);
              alert("Emergency reported! Thank you.");
            }}
          />
        )}
      </div>
    </div>
  );
}
