import React from "react";
import { useNavigate } from "react-router-dom";

export default function RoleSelectionPage() {
  const navigate = useNavigate();
  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-indigo-100 via-blue-50 to-purple-100 relative overflow-hidden">
      {/* Add background animation blobs here if you want */}
      <div className="z-10 flex flex-col items-center">
        <h2 className="text-4xl md:text-5xl font-black text-gray-900 mb-3 drop-shadow-md">
          How would you like to continue?
        </h2>
        <p className="text-gray-600 text-lg md:text-xl mb-10 font-medium">
          Choose the option that suits you best
        </p>
        <div className="flex flex-col gap-6 w-full max-w-xs">
          <button
            onClick={() => navigate("/user")}
            className="group relative px-8 py-6 bg-gradient-to-r from-blue-500 to-blue-700 text-white text-xl font-bold rounded-2xl shadow-lg hover:scale-105 hover:shadow-blue-500/40 transition-all duration-300"
          >
            üë§ User
          </button>
          <button
            onClick={() => navigate("/admin")}
            className="group relative px-8 py-6 bg-gradient-to-r from-orange-400 to-red-500 text-white text-xl font-bold rounded-2xl shadow-lg hover:scale-105 hover:shadow-orange-500/40 transition-all duration-300"
          >
            üõ°Ô∏è Admin
          </button>
          <button
            onClick={() => navigate("/signup")}
            className="group relative px-8 py-6 bg-gradient-to-r from-green-500 to-teal-500 text-white text-xl font-bold rounded-2xl shadow-lg hover:scale-105 hover:shadow-green-500/40 transition-all duration-300"
          >
            ‚úçÔ∏è Create Account
          </button>
        </div>
      </div>
    </div>
  );
}
