import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import SosModal from "./SosModal";

export default function UserDashboard() {
  const navigate = useNavigate();
  const currentUser = JSON.parse(localStorage.getItem("currentUser") || "{}");
  const [showSos, setShowSos] = useState(false);
  const [incidents, setIncidents] = useState([]);
  const [focus, setFocus] = useState("active");

  // live update every 3 seconds
  useEffect(() => {
    const fetchData = () => {
      const all = JSON.parse(localStorage.getItem("incidents") || "[]");
      const userReports = all.filter((item) => item.user === currentUser.email);
      setIncidents(userReports);
    };
    fetchData();
    const interval = setInterval(fetchData, 3000);
    return () => clearInterval(interval);
  }, [currentUser.email]);

  // Counts
  const activeAlerts = incidents.filter((x) => !x.resolved).length;
  const resolvedIncidents = incidents.filter((x) => x.resolved).length;
  const volunteers = 8; // static for now; can tie to backend later

  const handleLogout = () => {
    localStorage.removeItem("currentUser");
    navigate("/");
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-blue-100">
      {/* NAVBAR */}
      <nav className="bg-white shadow-md px-8 py-4 flex justify-between items-center">
        <h2 className="text-2xl font-black text-gray-900">Community Safety Hub</h2>
        <div className="flex gap-4 items-center">
          <span className="text-gray-700 font-semibold">Hi, {currentUser.name || "User"}!</span>
          <button
            onClick={() => navigate("/")}
            className="text-gray-700 font-semibold hover:text-blue-600 transition"
          >
            Home
          </button>
          <button
            onClick={handleLogout}
            className="px-6 py-2 bg-red-500 text-white font-bold rounded-lg hover:bg-red-600 transition"
          >
            Logout
          </button>
        </div>
      </nav>

      {/* BODY */}
      <div className="px-8 py-12">
        <h1 className="text-4xl font-black text-gray-900 mb-8">User Dashboard</h1>

        {/* LIVE STATS */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-12">
          <button
            className={`bg-white rounded-2xl shadow-xl p-6 hover:shadow-2xl transition text-left border-2 ${
              focus === "active" ? "border-blue-600" : "border-transparent"
            }`}
            onClick={() => setFocus("active")}
          >
            <div className="bg-blue-100 p-3 rounded-full w-fit mb-3">
              🚨
            </div>
            <h3 className="text-3xl font-bold text-gray-900">{activeAlerts}</h3>
            <p className="text-gray-600 text-sm">Active Alerts</p>
          </button>

          <button
            className={`bg-white rounded-2xl shadow-xl p-6 hover:shadow-2xl transition text-left border-2 ${
              focus === "resolved" ? "border-blue-600" : "border-transparent"
            }`}
            onClick={() => setFocus("resolved")}
          >
            <div className="bg-green-100 p-3 rounded-full w-fit mb-3">✅</div>
            <h3 className="text-3xl font-bold text-gray-900">{resolvedIncidents}</h3>
            <p className="text-gray-600 text-sm">Resolved Incidents</p>
          </button>

          <button
            className={`bg-white rounded-2xl shadow-xl p-6 hover:shadow-2xl transition text-left border-2 ${
              focus === "volunteer" ? "border-blue-600" : "border-transparent"
            }`}
            onClick={() => setFocus("volunteer")}
          >
            <div className="bg-purple-100 p-3 rounded-full w-fit mb-3">👥</div>
            <h3 className="text-3xl font-bold text-gray-900">{volunteers}</h3>
            <p className="text-gray-600 text-sm">Nearby Volunteers</p>
          </button>
        </div>

        {/* SOS BUTTON CENTERED */}
        <div className="flex justify-center my-10">
          <button
            onClick={() => setShowSos(true)}
            className="bg-gradient-to-r from-red-500 to-red-700 text-white font-extrabold py-6 px-12 rounded-2xl shadow-2xl hover:scale-105 transition text-2xl"
          >
            🚨 Report Emergency
          </button>
        </div>

        {/* RECENT REPORTS */}
        <div className="bg-white rounded-2xl shadow-lg p-8 mt-12">
          <h2 className="text-2xl font-bold text-gray-900 mb-6">Your Recent Reports</h2>
          {incidents.length === 0 ? (
            <div className="text-gray-500 text-center py-10">
              No incidents reported yet.
            </div>
          ) : (
            incidents
              .filter((x) =>
                focus === "resolved"
                  ? x.resolved
                  : focus === "active"
                  ? !x.resolved
                  : true
              )
              .map((incident) => (
                <div
                  key={incident.id}
                  className="border-b py-5 flex justify-between items-center"
                >
                  <div>
                    <p className="font-semibold capitalize">{incident.type}</p>
                    <p className="text-sm text-gray-500">{incident.time}</p>
                    <p className="text-gray-700">{incident.desc}</p>
                  </div>
                  <span
                    className={`px-4 py-1 text-sm rounded-full font-semibold ${
                      incident.urgency === "high"
                        ? "bg-red-500 text-white"
                        : incident.urgency === "medium"
                        ? "bg-yellow-400 text-black"
                        : "bg-green-500 text-white"
                    }`}
                  >
                    {incident.urgency}
                  </span>
                </div>
              ))
          )}
        </div>
      </div>

      {showSos && (
        <SosModal
          onClose={() => setShowSos(false)}
          onReported={(incident) => {
            const all = JSON.parse(localStorage.getItem("incidents") || "[]");
            const rep = {
              ...incident,
              user: currentUser.email,
              userName: currentUser.name,
              resolved: false,
            };
            localStorage.setItem("incidents", JSON.stringify([rep, ...all]));
            setShowSos(false);
          }}
        />
      )}
    </div>
  );
}
