import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const STORE_KEY = "emergencies";

export default function AdminDashboard() {
  const navigate = useNavigate();
  const [items, setItems] = useState([]);

  const signOut = () => {
    localStorage.removeItem("token");
    navigate("/", { replace: true });
  };

  useEffect(() => {
    const load = () => setItems(readStore());
    load();
    const id = setInterval(load, 1000);
    return () => clearInterval(id);
  }, []);

  const updateStatus = (id, next) => {
    const list = readStore().map((x) => (x.id === id ? { ...x, status: next } : x));
    writeStore(list);
    setItems(list);
  };

  return (
    <div className="min-h-screen bg-slate-50">
      <header className="mx-auto flex max-w-6xl items-center justify-between px-4 py-4">
        <div className="flex items-center gap-3">
          <div className="grid h-9 w-9 place-items-center rounded-xl bg-emerald-600 text-white">üõü</div>
          <div>
            <div className="text-lg font-semibold text-slate-800">Responder Console</div>
            <div className="text-xs text-slate-500">Volunteer / Admin</div>
          </div>
        </div>
        <button className="rounded-xl border border-slate-200 px-3 py-1.5 text-sm text-slate-700 hover:bg-slate-50" onClick={signOut}>
          Sign Out
        </button>
      </header>

      <main className="mx-auto max-w-6xl px-4 pb-10">
        <section className="rounded-3xl border border-slate-200 bg-white p-6 shadow-sm">
          <h2 className="text-xl font-semibold text-slate-900">Available Emergencies</h2>

          <div className="mt-4 space-y-4">
            {items.length === 0 && (
              <div className="rounded-xl border border-slate-100 bg-slate-50 p-4 text-slate-600">No emergencies yet. Stand by.</div>
            )}

            {items.map((x) => (
              <EmergencyCard key={x.id} item={x} onUpdate={updateStatus} />
            ))}
          </div>
        </section>
      </main>
    </div>
  );
}

/* helpers */
function readStore() {
  try {
    return JSON.parse(localStorage.getItem(STORE_KEY) || "[]");
  } catch {
    return [];
  }
}
function writeStore(list) {
  localStorage.setItem(STORE_KEY, JSON.stringify(list));
}
function toTime(ts) {
  const d = new Date(ts);
  return d.toLocaleString();
}

/* card */
function EmergencyCard({ item, onUpdate }) {
  const typeText =
    { medical: "medical", fire: "fire", crime: "crime", accident: "accident", other: "other" }[item.type] || "unknown";

  const statusText =
    { new: "New", accepted: "Accepted", responding: "Responding", done: "Done" }[item.status] || item.status;

  const openMaps = () => {
    if (item.lat && item.lng) {
      window.open(`https://www.google.com/maps?q=${item.lat},${item.lng}`, "_blank");
    } else {
      alert("No location available (user denied GPS).");
    }
  };

  const next = (() => {
    if (item.status === "new") return { label: "Accept & Respond", val: "accepted" };
    if (item.status === "accepted") return { label: "Start Responding", val: "responding" };
    if (item.status === "responding") return { label: "Mark Done", val: "done" };
    return null;
  })();

  return (
    <div className="rounded-2xl border border-slate-200 p-4 shadow-sm">
      <div className="flex flex-wrap items-center gap-3">
        <span className="rounded-full bg-rose-100 px-2 py-1 text-xs font-semibold text-rose-700">{typeText}</span>
        <span className="rounded-full bg-amber-100 px-2 py-1 text-xs font-semibold text-amber-700">High</span>
        <span className="text-slate-500">‚Ä¢ {toTime(item.createdAt)}</span>
        {item.lat && item.lng && (
          <span className="text-slate-500">‚Ä¢ {item.lat.toFixed(5)}, {item.lng.toFixed(5)}</span>
        )}
        <span className="ml-2 rounded-full bg-slate-100 px-2 py-1 text-xs font-semibold text-slate-700">{statusText}</span>
      </div>

      {item.note && <div className="mt-2 text-slate-700">{item.note}</div>}

      <div className="mt-3 flex flex-col gap-3 sm:flex-row">
        <button className="flex-1 rounded-xl border border-slate-200 bg-white px-4 py-2 font-medium text-slate-700 hover:bg-slate-50" onClick={openMaps}>
          üìç View Location
        </button>

        {next ? (
          <button
            className="flex-1 rounded-xl bg-blue-600 px-4 py-2 font-semibold text-white shadow hover:bg-blue-700"
            onClick={() => onUpdate(item.id, next.val)}
          >
            {next.label}
          </button>
        ) : (
          <button className="flex-1 cursor-default rounded-xl bg-emerald-600 px-4 py-2 font-semibold text-white shadow" disabled>
            Completed
          </button>
        )}
      </div>
    </div>
  );
}
