import React, { useState, useEffect } from 'react';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [incidents, setIncidents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [showMapModal, setShowMapModal] = useState(false);

  // Fetch all incidents (not filtered by user)
  const fetchIncidents = async () => {
    try {
      const response = await fetch('/api/incidents/all', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        }
      });
      const data = await response.json();
      
      // Sort incidents: active first, then by priority and time
      const sortedIncidents = data.sort((a, b) => {
        // Status sorting (active/pending before completed)
        if (a.status === 'completed' && b.status !== 'completed') return 1;
        if (a.status !== 'completed' && b.status === 'completed') return -1;
        
        // Priority sorting (High > Medium > Low)
        const priorityOrder = { 'High': 1, 'Medium': 2, 'Low': 3 };
        if (priorityOrder[a.priority] !== priorityOrder[b.priority]) {
          return priorityOrder[a.priority] - priorityOrder[b.priority];
        }
        
        // Time sorting (newest first)
        return new Date(b.created_at) - new Date(a.created_at);
      });
      
      setIncidents(sortedIncidents);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching incidents:', error);
      setLoading(false);
    }
  };

  // Auto-refresh every 30 seconds
  useEffect(() => {
    fetchIncidents();
    const interval = setInterval(fetchIncidents, 30000);
    return () => clearInterval(interval);
  }, []);

  // View location handler
  const handleViewLocation = (incident) => {
    setSelectedLocation({
      lat: incident.latitude,
      lng: incident.longitude,
      address: incident.location_address
    });
    setShowMapModal(true);
  };

  // Accept and respond handler
  const handleAcceptRespond = async (incidentId) => {
    try {
      const response = await fetch(`/api/incidents/${incidentId}/accept`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          status: 'responding',
          responder_id: localStorage.getItem('userId'),
          responded_at: new Date().toISOString()
        })
      });
      
      if (response.ok) {
        alert('You have accepted and are responding to this incident');
        fetchIncidents(); // Refresh list
      }
    } catch (error) {
      console.error('Error accepting incident:', error);
    }
  };

  // Mark complete handler
  const handleMarkComplete = async (incidentId) => {
    try {
      const response = await fetch(`/api/incidents/${incidentId}/complete`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          status: 'completed',
          completed_at: new Date().toISOString()
        })
      });
      
      if (response.ok) {
        alert('Incident marked as complete');
        fetchIncidents(); // Refresh list
      }
    } catch (error) {
      console.error('Error completing incident:', error);
    }
  };

  // Format date/time
  const formatDateTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString('en-IN', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Filter for last 7 days
  const isWithinLastWeek = (dateString) => {
    const incidentDate = new Date(dateString);
    const weekAgo = new Date();
    weekAgo.setDate(weekAgo.getDate() - 7);
    return incidentDate >= weekAgo;
  };

  // Split incidents into active and completed
  const activeIncidents = incidents.filter(
    inc => inc.status !== 'completed' && isWithinLastWeek(inc.created_at)
  );
  const completedIncidents = incidents.filter(
    inc => inc.status === 'completed' && isWithinLastWeek(inc.created_at)
  );

  return (
    <div className="admin-dashboard">
      <header className="dashboard-header">
        <h1>Admin Dashboard - Incident Management</h1>
        <div className="header-info">
          <span>Auto-refresh: ON (30s)</span>
          <span>Showing last 7 days</span>
        </div>
      </header>

      {loading ? (
        <div className="loading">Loading incidents...</div>
      ) : (
        <>
          {/* Active Incidents Section */}
          <section className="incidents-section active-section">
            <h2>Active Incidents ({activeIncidents.length})</h2>
            {activeIncidents.length === 0 ? (
              <p className="no-incidents">No active incidents</p>
            ) : (
              <div className="incidents-list">
                {activeIncidents.map(incident => (
                  <div key={incident.id} className="incident-card active">
                    <div className="incident-header">
                      <div className="incident-tags">
                        <span className={`tag-type ${incident.type}`}>
                          {incident.type}
                        </span>
                        <span className={`tag-priority ${incident.priority.toLowerCase()}`}>
                          {incident.priority}
                        </span>
                      </div>
                      <span className="incident-time">
                        {formatDateTime(incident.created_at)}
                      </span>
                    </div>
                    
                    <div className="incident-details">
                      <p className="incident-description">{incident.description}</p>
                      <p className="incident-reporter">
                        Reported by: {incident.reporter_name}
                      </p>
                      <p className="incident-location">
                        üìç {incident.latitude.toFixed(4)}, {incident.longitude.toFixed(4)}
                      </p>
                    </div>

                    <div className="incident-actions">
                      <button 
                        className="btn-view-location"
                        onClick={() => handleViewLocation(incident)}
                      >
                        üìç View Location
                      </button>
                      
                      {incident.status === 'pending' && (
                        <button 
                          className="btn-accept"
                          onClick={() => handleAcceptRespond(incident.id)}
                        >
                          ‚úì Accept & Respond
                        </button>
                      )}
                      
                      {incident.status === 'responding' && (
                        <button 
                          className="btn-complete"
                          onClick={() => handleMarkComplete(incident.id)}
                        >
                          ‚úì Mark Complete
                        </button>
                      )}
                    </div>

                    {incident.status === 'responding' && (
                      <div className="incident-status-badge responding">
                        Currently Responding - {incident.responder_name}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </section>

          {/* Completed Incidents Section */}
          <section className="incidents-section completed-section">
            <h2>Completed Incidents (Last 7 Days) ({completedIncidents.length})</h2>
            {completedIncidents.length === 0 ? (
              <p className="no-incidents">No completed incidents in the last week</p>
            ) : (
              <div className="incidents-list">
                {completedIncidents.map(incident => (
                  <div key={incident.id} className="incident-card completed">
                    <div className="incident-header">
                      <div className="incident-tags">
                        <span className={`tag-type ${incident.type}`}>
                          {incident.type}
                        </span>
                        <span className={`tag-priority ${incident.priority.toLowerCase()}`}>
                          {incident.priority}
                        </span>
                      </div>
                      <span className="incident-time">
                        {formatDateTime(incident.created_at)}
                      </span>
                    </div>
                    
                    <div className="incident-details">
                      <p className="incident-description">{incident.description}</p>
                      <p className="incident-completed-info">
                        ‚úì Completed by: {incident.responder_name} on {formatDateTime(incident.completed_at)}
                      </p>
                    </div>

                    <div className="incident-actions">
                      <button 
                        className="btn-view-location secondary"
                        onClick={() => handleViewLocation(incident)}
                      >
                        üìç View Location
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </section>
        </>
      )}

      {/* Map Modal */}
      {showMapModal && (
        <div className="modal-overlay" onClick={() => setShowMapModal(false)}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Incident Location</h3>
              <button className="close-btn" onClick={() => setShowMapModal(false)}>‚úï</button>
            </div>
            <div className="modal-body">
              <p><strong>Coordinates:</strong> {selectedLocation?.lat}, {selectedLocation?.lng}</p>
              {selectedLocation?.address && (
                <p><strong>Address:</strong> {selectedLocation.address}</p>
              )}
              {/* Google Maps iframe */}
              <iframe
                title="Incident Location"
                width="100%"
                height="400"
                frameBorder="0"
                src={`https://www.google.com/maps?q=${selectedLocation?.lat},${selectedLocation?.lng}&z=15&output=embed`}
              />
              <a 
                href={`https://www.google.com/maps?q=${selectedLocation?.lat},${selectedLocation?.lng}`}
                target="_blank"
                rel="noopener noreferrer"
                className="open-maps-link"
              >
                Open in Google Maps
              </a>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
