import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const STORE_KEY = "emergencies";

export default function UserDashboard() {
  const navigate = useNavigate();
  const [view, setView] = useState("sos"); // sos | modal | confirm
  const [selection, setSelection] = useState({ type: null, note: "" });

  const signOut = () => {
    localStorage.removeItem("token");
    navigate("/", { replace: true });
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-slate-50 to-white">
      {/* Top bar */}
      <header className="mx-auto flex max-w-6xl items-center justify-between px-4 py-4">
        <div className="flex items-center gap-3">
          <div className="grid h-9 w-9 place-items-center rounded-xl bg-blue-600 text-white">üõ°Ô∏è</div>
          <div>
            <div className="text-lg font-semibold text-slate-800">SafetyHub</div>
            <div className="text-xs text-slate-500">You ‚Ä¢ citizen</div>
          </div>
        </div>
        <button className="rounded-xl border border-slate-200 px-3 py-1.5 text-sm text-slate-700 hover:bg-slate-50" onClick={signOut}>
          Sign Out
        </button>
      </header>

      {/* SOS view */}
      {view === "sos" && (
        <>
          <section className="mx-auto grid max-w-6xl place-items-center px-4 py-6">
            <button
              onClick={() => setView("modal")}
              className="relative grid h-64 w-64 place-items-center rounded-full bg-gradient-to-b from-rose-500 to-rose-600 text-white shadow-2xl transition hover:scale-[1.02]"
            >
              <div className="pointer-events-none absolute inset-0 rounded-full bg-rose-400/20 blur-2xl" />
              <div className="text-[66px] leading-none">!</div>
              <div className="absolute bottom-10 text-2xl font-extrabold tracking-wide">SOS</div>
              <div className="absolute bottom-4 text-xs opacity-90">Tap for Emergency</div>
            </button>
          </section>

          <section className="mx-auto max-w-6xl px-4 pb-10">
            <div className="rounded-3xl border border-slate-200 bg-white p-6 shadow-sm">
              <h2 className="text-xl font-semibold text-slate-900">Your Recent Reports</h2>
              <EmptyState />
            </div>
          </section>
        </>
      )}

      {/* Picker */}
      {view === "modal" && (
        <PickerModal
          note={selection.note}
          setNote={(note) => setSelection((s) => ({ ...s, note }))}
          onPick={(type) => {
            setSelection((s) => ({ ...s, type }));
            setView("confirm");
          }}
          onClose={() => setView("sos")}
        />
      )}

      {/* Confirm */}
      {view === "confirm" && (
        <ConfirmView
          selection={selection}
          onBack={() => setView("modal")}
          onSend={async () => {
            await createEmergency(selection);
            setView("sos");
            alert("Emergency sent to responders.");
          }}
        />
      )}
    </div>
  );
}

/* -------------- Data helpers -------------- */
async function createEmergency(selection) {
  const coords = await getLocation();
  const item = {
    id: crypto.randomUUID(),
    type: selection.type,
    note: selection.note,
    status: "new", // new -> accepted -> responding -> done
    createdAt: Date.now(),
    lat: coords?.lat ?? null,
    lng: coords?.lng ?? null,
    priority: "high"
  };
  const list = readStore();
  list.unshift(item);
  writeStore(list);
}
function readStore() {
  try {
    return JSON.parse(localStorage.getItem(STORE_KEY) || "[]");
  } catch {
    return [];
  }
}
function writeStore(list) {
  localStorage.setItem(STORE_KEY, JSON.stringify(list));
}
function getLocation() {
  return new Promise((resolve) => {
    if (!navigator.geolocation) return resolve(null);
    navigator.geolocation.getCurrentPosition(
      (pos) => resolve({ lat: pos.coords.latitude, lng: pos.coords.longitude }),
      () => resolve(null),
      { enableHighAccuracy: true, timeout: 8000 }
    );
  });
}

/* -------------- UI components -------------- */
function EmptyState() {
  return (
    <div className="mt-12 grid place-items-center gap-2 py-10 text-slate-500">
      <div className="grid h-14 w-14 place-items-center rounded-full border border-slate-200 text-2xl">!</div>
      <div>No emergencies reported yet</div>
    </div>
  );
}

function PickerModal({ onClose, onPick, note, setNote }) {
  return (
    <div className="fixed inset-0 z-50 grid place-items-center bg-black/40 px-4" role="dialog" aria-modal="true" onClick={onClose}>
      <div className="w-full max-w-2xl rounded-3xl bg-white p-6 shadow-2xl" onClick={(e) => e.stopPropagation()}>
        <h3 className="text-2xl font-bold text-slate-900">Report Emergency</h3>
        <p className="text-slate-600">Select the type of emergency</p>
        <textarea
          placeholder="Brief description (optional)"
          className="mt-4 w-full rounded-xl border border-slate-200 px-3 py-2 outline-none focus:ring-4 focus:ring-blue-100"
          rows={3}
          value={note}
          onChange={(e) => setNote(e.target.value)}
        />
        <div className="mt-4 space-y-3">
          <EmergencyButton color="from-rose-600 to-rose-500" icon="üíì" label="Medical Emergency" onClick={() => onPick("medical")} />
          <EmergencyButton color="from-orange-600 to-orange-500" icon="üî•" label="Fire" onClick={() => onPick("fire")} />
          <EmergencyButton color="from-rose-700 to-rose-600" icon="üö®" label="Crime/Violence" onClick={() => onPick("crime")} />
          <EmergencyButton color="from-amber-600 to-amber-500" icon="üöë" label="Accident" onClick={() => onPick("accident")} />
          <EmergencyButton color="from-slate-700 to-slate-600" icon="‚ö†Ô∏è" label="Other Emergency" onClick={() => onPick("other")} />
        </div>
        <button className="mt-4 w-full rounded-xl border border-slate-200 bg-slate-50 px-4 py-2 font-medium text-slate-700 hover:bg-slate-100" onClick={onClose}>
          Cancel
        </button>
      </div>
    </div>
  );
}

function ConfirmView({ selection, onBack, onSend }) {
  const label = mapType(selection.type);
  return (
    <div className="mx-auto max-w-2xl px-4 py-10">
      <div className="rounded-3xl border border-slate-200 bg-white p-6 shadow-sm">
        <h3 className="text-2xl font-bold text-slate-900">Confirm Report</h3>
        <div className="mt-6 grid gap-3">
          <Row title="Type" value={label} />
          <Row title="Location" value="Sent with your report (GPS if allowed)" />
        </div>
        <div className="mt-6 flex flex-col gap-3 sm:flex-row">
          <button className="flex-1 rounded-xl border border-slate-200 bg-slate-50 px-4 py-2 font-medium text-slate-700 hover:bg-slate-100" onClick={onBack}>
            Back
          </button>
          <button className="flex-1 rounded-xl bg-blue-600 px-4 py-2 font-semibold text-white shadow hover:bg-blue-700" onClick={onSend}>
            Send SOS
          </button>
        </div>
      </div>
    </div>
  );
}

function Row({ title, value }) {
  return (
    <div className="grid gap-1 rounded-xl border border-slate-100 bg-slate-50/50 p-3">
      <div className="text-xs font-semibold uppercase tracking-wide text-slate-500">{title}</div>
      <div className="text-slate-800">{value}</div>
    </div>
  );
}

function EmergencyButton({ color, icon, label, onClick }) {
  return (
    <button onClick={onClick} className={`flex w-full items-center gap-3 rounded-2xl bg-gradient-to-r ${color} px-4 py-3 text-left text-white shadow hover:brightness-105 transition`}>
      <span className="grid h-9 w-9 place-items-center rounded-xl bg-white/20 text-xl">{icon}</span>
      <span className="text-base font-semibold">{label}</span>
    </button>
  );
}

function mapType(t) {
  return (
    {
      medical: "Medical Emergency",
      fire: "Fire",
      crime: "Crime/Violence",
      accident: "Accident",
      other: "Other Emergency"
    }[t] || "Emergency"
  );
}
