import React from "react";

export default function RoleSelectionPage({ onSelect }) {
  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-b from-blue-50 to-blue-200">
      <h2 className="text-4xl font-extrabold text-black mb-3">Who are you?</h2>
      <p className="text-gray-600 text-lg mb-10">Choose your role to continue</p>
      <div className="flex flex-wrap gap-6 justify-center">
        <button
          onClick={() => onSelect("user")}
          className="px-10 py-6 bg-blue-600 text-white text-xl font-bold rounded-2xl shadow-lg hover:bg-blue-700 hover:scale-105 transition-transform"
        >
          üë§ User
        </button>
        <button
          onClick={() => onSelect("admin")}
          className="px-10 py-6 bg-orange-500 text-white text-xl font-bold rounded-2xl shadow-lg hover:bg-orange-600 hover:scale-105 transition-transform"
        >
          üõ° Admin
        </button>
        <button
          onClick={() => onSelect("signup")}
          className="px-10 py-6 bg-green-600 text-white text-xl font-bold rounded-2xl shadow-lg hover:bg-green-700 hover:scale-105 transition-transform"
        >
          ‚úçÔ∏è Create Account
        </button>
      </div>
    </div>
  );
}
