import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const urgencyColors = {
  high: "bg-red-500 text-white",
  medium: "bg-yellow-400 text-gray-900",
  low: "bg-green-500 text-white",
};

export default function AdminDashboard() {
  const navigate = useNavigate();
  const [incidents, setIncidents] = useState([]);
  const [focus, setFocus] = useState("all");

  // Refresh every 3s
  useEffect(() => {
    const update = () => {
      const data = JSON.parse(localStorage.getItem("incidents") || "[]");
      setIncidents(data);
    };
    update();
    const interval = setInterval(update, 3000);
    return () => clearInterval(interval);
  }, []);

  const totalIncidents = incidents.length;
  const pending = incidents.filter(x => !x.resolved).length;
  const resolved = incidents.filter(x => x.resolved).length;
  const activeUsers = new Set(incidents.map(x => x.user)).size; // Unique

  const filtered = incidents.filter((x) => {
    if (focus === "resolved") return x.resolved;
    if (focus === "pending") return !x.resolved;
    return true;
  });

  const markResolved = (id) => {
    const updated = incidents.map((x) =>
      x.id === id ? { ...x, resolved: true } : x
    );
    localStorage.setItem("incidents", JSON.stringify(updated));
    setIncidents(updated);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 to-red-50">
      <nav className="bg-white shadow-md px-8 py-4 flex justify-between items-center">
        <h2 className="text-2xl font-black text-gray-900">Admin Portal</h2>
        <div className="flex gap-4 items-center">
          <button onClick={() => navigate("/")} className="text-gray-700 font-semibold hover:text-orange-600 transition">Home</button>
          <button
            onClick={() => {
              localStorage.removeItem("currentAdmin");
              navigate("/");
            }}
            className="px-6 py-2 bg-red-500 text-white font-semibold rounded-lg hover:bg-red-600 transition"
          >
            Logout
          </button>
        </div>
      </nav>

      <div className="px-8 py-12">
        <h1 className="text-4xl font-black text-gray-900 mb-10">Admin Dashboard</h1>

        {/* Live Stats */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-8 mb-14">
          <button
            className={`bg-white rounded-2xl shadow-lg p-6 border-2 ${
              focus === "all" ? "border-black" : "border-transparent"
            } hover:shadow-2xl`}
            onClick={() => setFocus("all")}
          >
            <div className="bg-red-100 p-3 rounded-full w-fit mb-3">🚨</div>
            <h3 className="text-3xl font-bold">{totalIncidents}</h3>
            <p>Total Incidents</p>
          </button>

          <button
            className={`bg-white rounded-2xl shadow-lg p-6 border-2 ${
              focus === "resolved" ? "border-black" : "border-transparent"
            } hover:shadow-2xl`}
            onClick={() => setFocus("resolved")}
          >
            <div className="bg-green-100 p-3 rounded-full w-fit mb-3">✅</div>
            <h3 className="text-3xl font-bold">{resolved}</h3>
            <p>Resolved Incidents</p>
          </button>

          <button
            className={`bg-white rounded-2xl shadow-lg p-6 border-2 ${
              focus === "pending" ? "border-black" : "border-transparent"
            } hover:shadow-2xl`}
            onClick={() => setFocus("pending")}
          >
            <div className="bg-yellow-100 p-3 rounded-full w-fit mb-3">⏳</div>
            <h3 className="text-3xl font-bold">{pending}</h3>
            <p>Pending Incidents</p>
          </button>

          <div className="bg-white rounded-2xl shadow-lg p-6 hover:shadow-2xl">
            <div className="bg-blue-100 p-3 rounded-full w-fit mb-3">👥</div>
            <h3 className="text-3xl font-bold">{activeUsers}</h3>
            <p>Active Users</p>
          </div>
        </div>

        {/* Incident Table */}
        <div className="bg-white rounded-2xl shadow-lg p-8">
          <h2 className="text-2xl font-bold text-gray-900 mb-8">
            {focus === "resolved"
              ? "Resolved Incidents"
              : focus === "pending"
              ? "Pending Incidents"
              : "All Incidents"}
          </h2>

          {filtered.length === 0 ? (
            <div className="text-gray-500 text-center py-10">
              No incidents in this category.
            </div>
          ) : (
            filtered.map((report) => (
              <div key={report.id} className="flex justify-between items-start border-b py-5">
                <div>
                  <p className="font-semibold text-lg capitalize">{report.type}</p>
                  <p className="text-gray-600 text-sm">{report.time}</p>
                  <p className="text-gray-700 mb-1">{report.desc}</p>
                  <span
                    className={`inline-block px-3 py-1 text-xs rounded-full font-semibold ${
                      urgencyColors[report.urgency] ||
                      "bg-gray-300 text-gray-800"
                    }`}
                  >
                    {report.urgency || "N/A"}
                  </span>
                  <p className="text-xs text-gray-500 mt-1">
                    User: {report.userName || "Unknown"}
                  </p>
                </div>
                {!report.resolved && (
                  <button
                    onClick={() => markResolved(report.id)}
                    className="bg-green-600 text-white px-5 py-2 rounded-lg hover:bg-green-700 transition"
                  >
                    Mark as Resolved
                  </button>
                )}
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}
