import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const urgencyColors = {
  high: "bg-red-500 text-white",
  medium: "bg-yellow-400 text-gray-900",
  low: "bg-green-500 text-white",
};

export default function AdminDashboard() {
  const navigate = useNavigate();
  const [incidents, setIncidents] = useState([]);
  const [users, setUsers] = useState([]);
  const [focus, setFocus] = useState("all");

  // Load incidents and users
  useEffect(() => {
    const inc = JSON.parse(localStorage.getItem("incidents") || "[]")
      .filter((x) => x && x.id); // filter invalid
    const us = JSON.parse(localStorage.getItem("users") || "[]");
    setIncidents(inc);
    setUsers(us);
  }, []);

  const totalIncidents = incidents.length;
  const resolvedIncidents = incidents.filter((x) => x.resolved).length;
  const pendingIncidents = incidents.filter((x) => !x.resolved).length;
  const activeUsers = users.length;

  const getFocusList = () => {
    if (focus === "resolved") return incidents.filter((x) => x.resolved);
    if (focus === "pending") return incidents.filter((x) => !x.resolved);
    if (focus === "users") return users;
    return incidents;
  };

  // Resolve functionality
  const handleResolve = (id) => {
    const updated = incidents.map((x) =>
      x.id === id ? { ...x, resolved: true } : x
    );
    localStorage.setItem("incidents", JSON.stringify(updated));
    setIncidents(updated);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 to-red-50">
      {/* NAVBAR */}
      <nav className="bg-white shadow-md px-8 py-4 flex justify-between items-center">
        <h2 className="text-2xl font-black text-gray-900">Admin Portal</h2>
        <div className="flex gap-4 items-center">
          <button
            onClick={() => navigate("/")}
            className="text-gray-700 font-semibold hover:text-orange-600 transition"
          >
            Home
          </button>
          <button
            onClick={() => {
              localStorage.removeItem("currentAdmin");
              navigate("/");
            }}
            className="px-6 py-2 bg-red-500 text-white font-bold rounded-lg hover:bg-red-600 transition"
          >
            Logout
          </button>
        </div>
      </nav>

      {/* MAIN CONTENT */}
      <div className="px-8 py-12">
        <h1 className="text-4xl font-black text-gray-900 mb-8">Admin Dashboard</h1>

        {/* LIVE-STATS CARDS */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-10">
          {/* Total Incidents */}
          <button
            className={`bg-white rounded-2xl shadow-lg p-6 hover:shadow-2xl transition text-left border-2 ${
              focus === "all" ? "border-black" : "border-transparent"
            }`}
            onClick={() => setFocus("all")}
          >
            <div className="bg-red-100 p-3 rounded-full w-fit mb-3">
              <span className="text-3xl">üö®</span>
            </div>
            <h3 className="text-3xl font-bold text-gray-900">{totalIncidents}</h3>
            <p className="text-gray-600 text-sm">Total Incidents</p>
          </button>

          {/* Resolved */}
          <button
            className={`bg-white rounded-2xl shadow-lg p-6 hover:shadow-2xl transition text-left border-2 ${
              focus === "resolved" ? "border-black" : "border-transparent"
            }`}
            onClick={() => setFocus("resolved")}
          >
            <div className="bg-green-100 p-3 rounded-full w-fit mb-3">
              <span className="text-3xl">‚úÖ</span>
            </div>
            <h3 className="text-3xl font-bold text-gray-900">{resolvedIncidents}</h3>
            <p className="text-gray-600 text-sm">Resolved</p>
          </button>

          {/* Pending */}
          <button
            className={`bg-white rounded-2xl shadow-lg p-6 hover:shadow-2xl transition text-left border-2 ${
              focus === "pending" ? "border-black" : "border-transparent"
            }`}
            onClick={() => setFocus("pending")}
          >
            <div className="bg-yellow-100 p-3 rounded-full w-fit mb-3">
              <span className="text-3xl">‚è≥</span>
            </div>
            <h3 className="text-3xl font-bold text-gray-900">{pendingIncidents}</h3>
            <p className="text-gray-600 text-sm">Pending</p>
          </button>

          {/* Users */}
          <button
            className={`bg-white rounded-2xl shadow-lg p-6 hover:shadow-2xl transition text-left border-2 ${
              focus === "users" ? "border-black" : "border-transparent"
            }`}
            onClick={() => setFocus("users")}
          >
            <div className="bg-blue-100 p-3 rounded-full w-fit mb-3">
              <span className="text-3xl">üë•</span>
            </div>
            <h3 className="text-3xl font-bold text-gray-900">{activeUsers}</h3>
            <p className="text-gray-600 text-sm">Active Users</p>
          </button>
        </div>

        {/* DATA DISPLAY */}
        <div className="bg-white rounded-2xl shadow-lg p-8">
          {focus === "users" ? (
            <>
              <h2 className="text-2xl font-bold text-gray-900 mb-6">User Accounts</h2>
              {users.length === 0 ? (
                <div className="text-gray-500 text-center py-10">No users found.</div>
              ) : (
                users.map((u, i) => (
                  <div key={i} className="border-b py-4 flex justify-between">
                    <span className="font-semibold">{u.name}</span>
                    <span className="text-sm text-gray-500">{u.email}</span>
                  </div>
                ))
              )}
            </>
          ) : (
            <>
              <h2 className="text-2xl font-bold text-gray-900 mb-6">
                {focus === "resolved"
                  ? "Resolved Incidents"
                  : focus === "pending"
                  ? "Pending Incidents"
                  : "All Incidents"}
              </h2>

              {getFocusList().length === 0 ? (
                <div className="text-gray-500 text-center py-10">
                  No incidents to display.
                </div>
              ) : (
                getFocusList()
                  .sort((a, b) => {
                    const order = { high: 3, medium: 2, low: 1 };
                    return (order[b.urgency] || 0) - (order[a.urgency] || 0);
                  })
                  .map((incident) => (
                    <div
                      key={incident.id}
                      className="flex flex-col md:flex-row md:items-center justify-between border-b py-5"
                    >
                      <div>
                        <div className="flex items-center gap-3">
                          <span className="font-semibold text-lg capitalize">
                            {incident.type || "Unknown"}
                          </span>
                          <span
                            className={`px-3 py-1 font-semibold rounded-full text-xs ${
                              urgencyColors[incident.urgency] ||
                              "bg-gray-300 text-gray-700"
                            }`}
                          >
                            {incident.urgency
                              ? incident.urgency.charAt(0).toUpperCase() +
                                incident.urgency.slice(1)
                              : "N/A"}{" "}
                            Urgency
                          </span>
                          {incident.resolved && (
                            <span className="px-2 py-1 bg-green-100 text-green-700 rounded-full text-xs font-semibold">
                              Resolved
                            </span>
                          )}
                        </div>
                        <p className="text-sm text-gray-500">{incident.time}</p>
                        {incident.desc && (
                          <p className="text-gray-700 mt-1">{incident.desc}</p>
                        )}
                        {incident.location && (
                          <p className="text-xs text-blue-600">
                            Lat: {incident.location.lat.toFixed(4)} | Lng:{" "}
                            {incident.location.lng.toFixed(4)}
                          </p>
                        )}
                        <p className="text-xs text-gray-500 mt-1">
                          Reported by: {incident.user || "Unknown User"}
                        </p>
                      </div>
                      {!incident.resolved && (
                        <button
                          onClick={() => handleResolve(incident.id)}
                          className="mt-4 md:mt-0 bg-green-600 hover:bg-green-700 text-white font-semibold px-6 py-2 rounded-lg transition"
                        >
                          Mark as Resolved
                        </button>
                      )}
                    </div>
                  ))
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
}
