import React, { useState, useEffect } from 'react';

const AdminDashboard = () => {
  const [incidents, setIncidents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [showMapModal, setShowMapModal] = useState(false);

  // Fetch all incidents (not filtered by user)
  const fetchIncidents = async () => {
    try {
      const response = await fetch('http://localhost:4000/api/incidents/all', {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        }
      });
      const data = await response.json();
      setIncidents(data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching incidents:', error);
      setLoading(false);
    }
  };

  // Auto-refresh every 30 seconds
  useEffect(() => {
    fetchIncidents();
    const interval = setInterval(fetchIncidents, 30000);
    return () => clearInterval(interval);
  }, []);

  // View location handler
  const handleViewLocation = (incident) => {
    setSelectedLocation({
      lat: incident.latitude,
      lng: incident.longitude,
      address: incident.location_address,
      type: incident.type
    });
    setShowMapModal(true);
  };

  // Accept and respond handler
  const handleAcceptRespond = async (incidentId) => {
    try {
      const userId = localStorage.getItem('userId');
      const response = await fetch(`http://localhost:4000/api/incidents/${incidentId}/accept`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          status: 'responding',
          responder_id: userId,
          responded_at: new Date().toISOString()
        })
      });
      
      if (response.ok) {
        alert('✅ You have accepted and are responding to this incident');
        fetchIncidents();
      } else {
        alert('❌ Failed to accept incident');
      }
    } catch (error) {
      console.error('Error accepting incident:', error);
      alert('❌ Error accepting incident');
    }
  };

  // Mark complete handler
  const handleMarkComplete = async (incidentId) => {
    try {
      const response = await fetch(`http://localhost:4000/api/incidents/${incidentId}/complete`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          status: 'completed',
          completed_at: new Date().toISOString()
        })
      });
      
      if (response.ok) {
        alert('✅ Incident marked as complete');
        fetchIncidents();
      } else {
        alert('❌ Failed to mark incident as complete');
      }
    } catch (error) {
      console.error('Error completing incident:', error);
      alert('❌ Error completing incident');
    }
  };

  // Format date/time
  const formatDateTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString('en-IN', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Split incidents into active and completed
  const activeIncidents = incidents.filter(inc => inc.status !== 'completed');
  const completedIncidents = incidents.filter(inc => inc.status === 'completed');

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      {/* Header */}
      <div className="bg-gradient-to-r from-purple-600 to-indigo-700 rounded-xl shadow-lg p-8 mb-8">
        <div className="flex justify-between items-center flex-wrap gap-4">
          <div>
            <h1 className="text-3xl font-bold text-white mb-2">Admin Dashboard</h1>
            <p className="text-purple-100">Incident Management System</p>
          </div>
          <div className="flex gap-4">
            <span className="bg-white bg-opacity-20 text-white px-4 py-2 rounded-full text-sm font-semibold">
              🔄 Auto-refresh: 30s
            </span>
            <span className="bg-white bg-opacity-20 text-white px-4 py-2 rounded-full text-sm font-semibold">
              📅 Last 7 days
            </span>
          </div>
        </div>
      </div>

      {loading ? (
        <div className="text-center py-20">
          <div className="animate-spin rounded-full h-16 w-16 border-b-4 border-purple-600 mx-auto"></div>
          <p className="mt-4 text-gray-600 text-lg">Loading incidents...</p>
        </div>
      ) : (
        <>
          {/* Active Incidents Section */}
          <div className="mb-10">
            <div className="flex items-center gap-3 mb-6">
              <h2 className="text-2xl font-bold text-red-600">🚨 Active Incidents</h2>
              <span className="bg-red-100 text-red-600 px-4 py-1 rounded-full font-semibold">
                {activeIncidents.length}
              </span>
            </div>

            {activeIncidents.length === 0 ? (
              <div className="bg-white rounded-xl shadow-md p-12 text-center">
                <p className="text-gray-400 text-lg">✓ No active incidents</p>
              </div>
            ) : (
              <div className="grid gap-6">
                {activeIncidents.map(incident => (
                  <div 
                    key={incident.id} 
                    className="bg-white rounded-xl shadow-md hover:shadow-xl transition-all duration-300 overflow-hidden border-l-4 border-red-500"
                  >
                    <div className="p-6">
                      {/* Header */}
                      <div className="flex justify-between items-start mb-4 flex-wrap gap-3">
                        <div className="flex gap-2 flex-wrap">
                          <span className={`px-3 py-1 rounded-full text-xs font-semibold ${
                            incident.type === 'medical' ? 'bg-red-100 text-red-700' :
                            incident.type === 'fire' ? 'bg-orange-100 text-orange-700' :
                            'bg-yellow-100 text-yellow-700'
                          }`}>
                            {incident.type}
                          </span>
                          <span className={`px-3 py-1 rounded-full text-xs font-semibold ${
                            incident.priority === 'High' ? 'bg-red-100 text-red-700' :
                            incident.priority === 'Medium' ? 'bg-orange-100 text-orange-700' :
                            'bg-green-100 text-green-700'
                          }`}>
                            {incident.priority} Priority
                          </span>
                        </div>
                        <span className="text-sm text-gray-500 font-medium">
                          🕐 {formatDateTime(incident.created_at)}
                        </span>
                      </div>

                      {/* Details */}
                      <div className="mb-4 space-y-2">
                        <p className="text-gray-800 text-base font-medium">{incident.description}</p>
                        <p className="text-sm text-gray-600">
                          👤 Reported by: <span className="font-semibold">{incident.reporter_name || 'Unknown'}</span>
                        </p>
                        <p className="text-sm text-gray-600">
                          📍 Location: {incident.latitude?.toFixed(4)}, {incident.longitude?.toFixed(4)}
                        </p>
                      </div>

                      {/* Status Badge for Responding */}
                      {incident.status === 'responding' && (
                        <div className="bg-green-50 border border-green-200 rounded-lg p-3 mb-4">
                          <p className="text-green-800 font-semibold text-sm">
                            ✓ Currently Responding - {incident.responder_name || 'Admin'}
                          </p>
                        </div>
                      )}

                      {/* Action Buttons */}
                      <div className="flex gap-3 flex-wrap">
                        <button
                          onClick={() => handleViewLocation(incident)}
                          className="px-6 py-2.5 bg-blue-500 hover:bg-blue-600 text-white rounded-lg font-semibold text-sm transition-colors duration-200 flex items-center gap-2"
                        >
                          📍 View Location
                        </button>
                        
                        {incident.status === 'pending' && (
                          <button
                            onClick={() => handleAcceptRespond(incident.id)}
                            className="px-6 py-2.5 bg-green-500 hover:bg-green-600 text-white rounded-lg font-semibold text-sm transition-colors duration-200 flex items-center gap-2"
                          >
                            ✓ Accept & Respond
                          </button>
                        )}
                        
                        {incident.status === 'responding' && (
                          <button
                            onClick={() => handleMarkComplete(incident.id)}
                            className="px-6 py-2.5 bg-orange-500 hover:bg-orange-600 text-white rounded-lg font-semibold text-sm transition-colors duration-200 flex items-center gap-2"
                          >
                            ✓ Mark Complete
                          </button>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Completed Incidents Section */}
          <div>
            <div className="flex items-center gap-3 mb-6">
              <h2 className="text-2xl font-bold text-green-600">✅ Completed Incidents</h2>
              <span className="bg-green-100 text-green-600 px-4 py-1 rounded-full font-semibold">
                {completedIncidents.length}
              </span>
            </div>

            {completedIncidents.length === 0 ? (
              <div className="bg-white rounded-xl shadow-md p-12 text-center">
                <p className="text-gray-400 text-lg">No completed incidents in the last week</p>
              </div>
            ) : (
              <div className="grid gap-6">
                {completedIncidents.map(incident => (
                  <div 
                    key={incident.id} 
                    className="bg-white rounded-xl shadow-md hover:shadow-lg transition-all duration-300 overflow-hidden border-l-4 border-green-500 opacity-80"
                  >
                    <div className="p-6">
                      <div className="flex justify-between items-start mb-4 flex-wrap gap-3">
                        <div className="flex gap-2 flex-wrap">
                          <span className={`px-3 py-1 rounded-full text-xs font-semibold ${
                            incident.type === 'medical' ? 'bg-red-100 text-red-700' :
                            incident.type === 'fire' ? 'bg-orange-100 text-orange-700' :
                            'bg-yellow-100 text-yellow-700'
                          }`}>
                            {incident.type}
                          </span>
                          <span className={`px-3 py-1 rounded-full text-xs font-semibold ${
                            incident.priority === 'High' ? 'bg-red-100 text-red-700' :
                            incident.priority === 'Medium' ? 'bg-orange-100 text-orange-700' :
                            'bg-green-100 text-green-700'
                          }`}>
                            {incident.priority} Priority
                          </span>
                        </div>
                        <span className="text-sm text-gray-500 font-medium">
                          🕐 {formatDateTime(incident.created_at)}
                        </span>
                      </div>

                      <div className="mb-4 space-y-2">
                        <p className="text-gray-800 text-base font-medium">{incident.description}</p>
                        <p className="text-sm text-green-700 font-semibold">
                          ✓ Completed by: {incident.responder_name || 'Admin'} on {formatDateTime(incident.completed_at)}
                        </p>
                      </div>

                      <button
                        onClick={() => handleViewLocation(incident)}
                        className="px-6 py-2.5 bg-gray-400 hover:bg-gray-500 text-white rounded-lg font-semibold text-sm transition-colors duration-200 flex items-center gap-2"
                      >
                        📍 View Location
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </>
      )}

      {/* Map Modal */}
      {showMapModal && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-70 flex justify-center items-center z-50 p-4"
          onClick={() => setShowMapModal(false)}
        >
          <div 
            className="bg-white rounded-2xl w-full max-w-4xl max-h-[90vh] overflow-auto shadow-2xl"
            onClick={e => e.stopPropagation()}
          >
            <div className="flex justify-between items-center p-6 border-b">
              <h3 className="text-2xl font-bold text-gray-800">📍 Incident Location</h3>
              <button
                onClick={() => setShowMapModal(false)}
                className="text-gray-400 hover:text-gray-600 text-3xl font-bold transition-colors"
              >
                ✕
              </button>
            </div>

            <div className="p-6 space-y-4">
              <div className="bg-gray-50 rounded-lg p-4 space-y-2">
                <p className="text-gray-700">
                  <span className="font-semibold">Type:</span> {selectedLocation?.type}
                </p>
                <p className="text-gray-700">
                  <span className="font-semibold">Coordinates:</span> {selectedLocation?.lat?.toFixed(4)}, {selectedLocation?.lng?.toFixed(4)}
                </p>
                {selectedLocation?.address && (
                  <p className="text-gray-700">
                    <span className="font-semibold">Address:</span> {selectedLocation.address}
                  </p>
                )}
              </div>

              <div className="rounded-lg overflow-hidden shadow-md">
                <iframe
                  title="Incident Location"
                  width="100%"
                  height="400"
                  frameBorder="0"
                  src={`https://www.google.com/maps?q=${selectedLocation?.lat},${selectedLocation?.lng}&z=15&output=embed`}
                  className="w-full"
                />
              </div>

              <a 
                href={`https://www.google.com/maps?q=${selectedLocation?.lat},${selectedLocation?.lng}`}
                target="_blank"
                rel="noopener noreferrer"
                className="block w-full text-center bg-blue-500 hover:bg-blue-600 text-white font-semibold py-3 rounded-lg transition-colors duration-200"
              >
                🗺️ Open in Google Maps
              </a>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
