import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";

export default function AdminLogin() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    // TODO: call backend then:
    navigate("/admin-dashboard");
  };

  return (
    <div className="min-h-screen grid place-items-center bg-gradient-to-b from-amber-50 to-white">
      <div className="w-[92vw] max-w-md">
        <div className="mb-4 text-center">
          <Link to="/" className="text-slate-500 hover:text-slate-800">‚Üê Back</Link>
        </div>

        <div className="relative">
          <div className="absolute -inset-3 rounded-3xl bg-amber-200/50 blur-2xl" />
          <div className="relative rounded-3xl border border-white/50 bg-white/70 p-6 shadow-2xl backdrop-blur-md">
            <h2 className="mb-1 text-center text-2xl font-bold text-amber-600">Volunteer / Admin Login</h2>
            <p className="mb-4 text-center text-slate-600">Manage incidents and assignments</p>

            <form onSubmit={handleSubmit} className="grid gap-3">
              <input
                type="email"
                placeholder="Admin Email"
                className="rounded-xl border border-slate-200 bg-white px-3 py-2 outline-none ring-amber-100 focus:ring-4"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <input
                type="password"
                placeholder="Admin Password"
                className="rounded-xl border border-slate-200 bg-white px-3 py-2 outline-none ring-amber-100 focus:ring-4"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <button className="mt-1 rounded-xl bg-amber-500 px-4 py-2 font-semibold text-white shadow hover:bg-amber-600 transition">
                Login
              </button>
            </form>

            <div className="mt-3 text-center text-sm text-slate-600">
              Need an account? <Link className="text-blue-700 hover:underline" to="/signup">Create an account</Link>
            </div>
            <div className="mt-1 text-center text-sm text-slate-600">
              User login? <Link className="text-blue-700 hover:underline" to="/login">Go here</Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
